import React, { useState } from "react";

export default function BracketValidator() {
  const [inputString, setInputString] = useState("");
  const [history, setHistory] = useState([]);

  function handleSubmit(event) {
    event.preventDefault();
    const stack = [];
    let missingClosing = null;
    const openingBracketStack = [];

    const map = {
      '(': ')',
      '[': ']',
      '{': '}',
      '<': '>',
    }

    const map2 = {
      ')': '(',
      ']': '[',
      '}': '{',
      '>': '<',
    }

    var isBracketValid = function(s) {   


      for (let i = 0 ; i < s.length ; i++) {
          let c = s[i];
          if (map[c]) {
            stack.push(map[c])
          }
          else{
            var potentialClosing = stack.pop();
            if (c!=potentialClosing){
              missingClosing = potentialClosing;
              return false;
            }
          }
      }
      return !stack.length;
  };

    if (isBracketValid(inputString)) {
      setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: "Valid" }]);
    } else if (missingClosing!==null){
      var openingpair = String(map[missingClosing]);
      let str = 'Invalid opening bracket ' + openingpair +', missing closing bracket '+ missingClosing
      setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: str}]);
    }
    else if (stack.length){
      var missingOpening = stack.pop();
      var closingPair = map2[missingOpening];
      let str = 'Invalid opening bracket ' + closingPair +', missing closing bracket '+ missingOpening
      setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: str}]);
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Input String:
          <textarea value={inputString} onChange={(event) => setInputString(event.target.value)} />
        </label>
        <button type="submit">Submit</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>Input String</th>
            <th>Reason</th>
          </tr>
        </thead>
        <tbody>
          {history.map((item, index) => (
            <tr key={index}>
              <td>{item.string}</td>
              <td>{item.reason}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}
