import React, { useState } from "react";

export default function BracketValidator() {
  const [inputString, setInputString] = useState("");
  const [history, setHistory] = useState([]);

  function handleSubmit(event) {
    event.preventDefault();
    const openingBrackets = ["[", "{", "("];
    const closingBrackets = ["]", "}", ")"];
    const openingBracketStack = [];

    const isBracketValid = (bracket) => {
      const openingIndex = openingBrackets.indexOf(bracket);
      const closingIndex = closingBrackets.indexOf(bracket);

      if (openingIndex === -1 || closingIndex === -1) {
        return false;
      }

      return openingIndex === closingIndex;
    };

    const isStringValid = (string) => {
      for (let i = 0; i < string.length; i++) {
        const bracket = string[i];

        if (isBracketValid(bracket)) {
          if (openingBrackets.indexOf(bracket) !== -1) {
            openingBracketStack.push(bracket);
          } else {
            if (openingBracketStack.length === 0) {
              return false;
            }

            if (!isBracketValid(openingBracketStack.pop())) {
              return false;
            }
          }
        }
      }

      return openingBracketStack.length === 0;
    };

    if (isStringValid(inputString)) {
      setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: "Valid" }]);
    } else {
      setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: "Invalid" }]);
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Input String:
          <textarea value={inputString} onChange={(event) => setInputString(event.target.value)} />
        </label>
        <button type="submit">Submit</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>Input String</th>
            <th>Reason</th>
          </tr>
        </thead>
        <tbody>
          {history.map((item, index) => (
            <tr key={index}>
              <td>{item.string}</td>
              <td>{item.reason}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}
