import React, { useState } from "react";

export default function BracketValidator() {
  const [inputString, setInputString] = useState("");
  const [history, setHistory] = useState([]);

  function handleSubmit(event) {
    event.preventDefault();
    const openingBrackets = ["[", "{", "(", "<"];
    const closingBrackets = ["]", "}", ")", ">"];
    const bracketStack = [];

    const brackets = inputString.match(/[{}\[\]()]|<[^>]*>/g);

    for (let i = 0; i < brackets.length; i++) {
      const bracket = brackets[i];
      const openingIndex = openingBrackets.indexOf(bracket);
      const closingIndex = closingBrackets.indexOf(bracket);

      if (openingIndex !== -1) {
        bracketStack.push(bracket);
      } else if (closingIndex !== -1) {
        if (bracketStack.length === 0) {
          setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: `Invalid closing bracket: ${bracket}` }]);
          return;
        } else if (closingIndex !== openingBrackets.indexOf(bracketStack.pop())) {
          setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: `Invalid closing bracket: ${bracket}` }]);
          return;
        }
      }
    }

    if (bracketStack.length !== 0) {
      setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: `Invalid opening bracket: ${bracketStack.pop()}` }]);
      return;
    }

    setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: "Valid" }]);
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Input String:
          <textarea value={inputString} onChange={(event) => setInputString(event.target.value)} />
        </label>
        <button type="submit">Submit</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>Input String</th>
            <th>Reason</th>
          </tr>
        </thead>
        <tbody>
          {history.map((item, index) => (
            <tr key={index}>
              <td>{item.string}</td>
              <td>{item.reason}</td>
            </tr>
          ))}
        </tbody>
        </table>
        </div>
  )
          }
