import React, { useState } from "react";

class Client {
  constructor(firstName, lastName, dob, email, address, status) {
      this.firstName = firstName;
      this.lastName = lastName;
      this.dob = dob;
      this.email = email;
      this.address = address;
      this.status = status;
  }
}

class ClientManager {
  constructor() {
      this.clients = [];
  }

  addClient(firstName, lastName, dob, email, address, status) {
      const client = new Client(firstName, lastName, dob, email, address, status);
      this.clients.push(client);
  }

  updateClient(firstName, lastName, fieldsToUpdate) {
      let client = this.clients.find(client => client.firstName === firstName && client.lastName === lastName);
      if (client) {
          Object.keys(fieldsToUpdate).forEach(key => {
              if (client.hasOwnProperty(key)) {
                  client[key] = fieldsToUpdate[key];
              }
          });
      } else {
          console.log('Client not found');
      }
  }

  viewAllClients() {
      return this.clients;
  }

  filterClientsByStatus(status) {
      return this.clients.filter(client => client.status === status);
  }
}

// usage
let manager = new ClientManager();
manager.addClient('John', 'Doe', '1990-01-01', 'john.doe@example.com', '123 Main St', 'New');
manager.addClient('Jane', 'Doe', '1992-01-01', 'jane.doe@example.com', '456 Main St', 'Active');
manager.addClient('Jim', 'Doe', '1993-01-01', 'jim.doe@example.com', '789 Main St', 'Inactive');

manager.updateClient('John', 'Doe', {status: 'Active', email: 'new.email@example.com'});

console.log(manager.viewAllClients());
console.log(manager.filterClientsByStatus('Active'));



export default function BracketValidator() {
  const [inputString, setInputString] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [dob, setDob] = useState("");
  const [email, setaddress] = useState("");
  const [status, setStatus] = useState("");

  const [history, setHistory] = useState([]);

  function handleSubmit(event) {
    event.preventDefault();
    const stack = [];
    var missing = null;
    const openingBracketStack = [];

    var map = {
      '(': ')',
      '[': ']',
      '{': '}',
      '<': '>',
    }

    var isBracketValid = function(s) {   

      
      for (let i = 0 ; i < s.length ; i++) {
          let c = s[i];
          if (map[c]) {
            stack.push(map[c])
          }
          else{
            var potential = stack[stack.length-1];
            if (c!=potential){
              missing = c;
              return false;
            }
            stack.pop();
          }
      }
      return !stack.length;
  };

    if (isBracketValid(inputString)) {
      setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: "Valid" }]);
    }
    else if (missing){
      if (map[missing]){
        var openingpair = map[missing];
        let str = 'Invalid opening bracket ' + missing +', missing closing bracket  '+ openingpair
        setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: str}]);
      }
      else if (map2[missing]){
        var prev = stack[stack.length-1];
        let str = null;
        if (prev && missing != map2[prev]){

           str = 'Invalid closing bracket ' + missing +', missing closing bracket  '+ prev

        }
        else{
           str = 'Invalid closing bracket ' + missing +', missing opening bracket  '+ map2[missing]

        }
        setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: str}]);
      }
    }
    else if (stack.length){
      var missingClosing = stack[0];
      var closingPair = map2[missingClosing];
      let str = ' Invalid opening bracket ' + closingPair +', missing closing bracket  '+ missingClosing
      setHistory((prevHistory) => [...prevHistory, { string: inputString, reason: str}]);
    }
    
  }

  return (
    <div >
      <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/water.css@2/out/water.css"/>
      <form onSubmit={handleSubmit}>
        <h1>
          Client Information
        </h1>
        <h2>
          Enter text for bracket valudation:
          <textarea value={inputString} onChange={(event) => setInputString(event.target.value)} />
        </h2>
        <h2>
          Enter text for bracket valudation:
          <textarea value={inputString} onChange={(event) => setInputString(event.target.value)} />
        </h2>
        <h2>
          Enter text for bracket valudation:
          <textarea value={inputString} onChange={(event) => setInputString(event.target.value)} />
        </h2>
        <div>
        <button type="submit">Submit</button>
        </div>
      </form>
      <table>

            <th>Input String</th>
            <th>Reason</th>
        
        <tbody>
          {history.map((item, index) => (
            <tr key={index}>
              <td>{item.string}</td>
              <td>{item.reason}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}
